import React, { useState, useEffect } from "react";
import axios from "axios";
import { format } from 'date-fns';
import ReactAnimatedWeather from "react-animated-weather";
import apiKeys from "./apiKeys";

function Forecast(props) {
  const [query, setQuery] = useState("");
  const [error, setError] = useState("");
  const [weather, setWeather] = useState({});
  const [localTime, setLocalTime] = useState('');

  const search = (city) => {
    axios
      .get(
        `${apiKeys.base}weather?q=${city != "[object Object]" ? city : query}&units=metric&APPID=${apiKeys.key}`
      )
      .then((response) => {
        setWeather(response.data);
        setQuery('');
        const timezoneOffset = response.data.timezone;
        const localDateTime = new Date(new Date().getTime() + timezoneOffset * 1000);
        const formattedLocalTime = format(localDateTime, 'HH:mm:ss');
        setLocalTime(formattedLocalTime);
      })
      .catch(function (error) {
        console.log(error);
        setWeather('');
        setQuery('');
        setError({ message: 'Not Found', query: query });
      });
  };

  useEffect(() => {
    search("Toronto");

    // Periodically update local time (every 1 minute in this example)
    const intervalId = setInterval(() => {
      search(query);
    }, 60000);

    // Cleanup the interval when the component is unmounted
    return () => clearInterval(intervalId);
  }, [query]);

  const defaults = {
    color: "white",
    size: 112,
    animate: true,
  };

  return (
    <div className="forecast">
      <div className="forecast-icon">
        <ReactAnimatedWeather
          icon={props.icon}
          color={defaults.color}
          size={defaults.size}
          animate={defaults.animate}
        />
      </div>
      <div className="today-weather">
        <h3>{props.weather}</h3>
        <div className="search-box">
          <input
            type="text"
            className="search-bar"
            placeholder="Search any city"
            onChange={(e) => setQuery(e.target.value)}
            value={query}
          />
          <div className="img-box">
            {" "}
            <img
              src="https://images.avishkaar.cc/workflow/newhp/search-white.png"
              onClick={() => search(query)}
            />
          </div>
        </div>
        <ul>
          {typeof weather.main != "undefined" ? (
            <div>
              <li className="cityHead">
                <p>
                  {weather.name}, {weather.sys.country}
                </p>
                <img
                  className="temp"
                  src={`https://openweathermap.org/img/wn/${weather.weather[0].icon}.png`}
                />
              </li>

              <li>
                Temperature{" "}
                <span className="temp">
                  {Math.round(weather.main.temp)}Â°c ({weather.weather[0].main})
                </span>
              </li>
              <li>
                Humidity{" "}
                <span className="temp">
                  {Math.round(weather.main.humidity)}%
                </span>
              </li>
              <li>
                Visibility{" "}
                <span className="temp">
                  {Math.round(weather.visibility)} mi
                </span>
              </li>
              <li>
                Wind Speed{" "}
                <span className="temp">
                  {Math.round(weather.wind.speed)} Km/h
                </span>
              </li>
            </div>
          ) : (
            <li>
              {error.query} {error.message}
            </li>
          )}
        </ul>
      </div>
    </div>
  );
}

export default Forecast;
