import React, { useState, useEffect, Component } from "react";
import axios from "axios";
import apiKeys from "./apiKeys";
import ReactAnimatedWeather from "react-animated-weather";
import Autosuggest from "react-autosuggest";

function Forcast(props) {
  const [query, setQuery] = useState("");
  const [error, setError] = useState("");
  const [weather, setWeather] = useState({});
  const [suggestions, setSuggestions] = useState([]);

  const onSuggestionsFetchRequested = ({ value }) => {
    axios
      .get(
        `${apiKeys.base}find?q=${value}&type=like&sort=population&cnt=5&APPID=${apiKeys.key}`
      )
      .then((response) => {
        const suggestions = response.data.list.map((item) => item.name);
        setSuggestions(suggestions);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  const onSuggestionsClearRequested = () => {
    setSuggestions([]);
  };

  const getSuggestionValue = (suggestion) => suggestion;

  const renderSuggestion = (suggestion) => <div>{suggestion}</div>;

  const search = () => {
    axios
      .get(
        `${apiKeys.base}weather?q=${query}&units=metric&APPID=${apiKeys.key}`
      )
      .then((response) => {
        setWeather(response.data);
        setQuery("");
        setSuggestions([]); // Clear suggestions after selecting a city
      })
      .catch(function (error) {
        console.log(error);
        setWeather("");
        setQuery("");
        setError({ message: "Not Found", query: query });
      });
  };

  useEffect(() => {
    search("Toronto");
  }, []);

  return (
    <div className="forecast">
      <div className="forecast-icon">
        <ReactAnimatedWeather
          icon={props.icon}
